import { Component, OnInit } from '@angular/core';
import { NzMessageService } from 'ng-zorro-antd/message';
import { FavoriteAdvertisement } from '../../models/favorite-ad.model';
import { GetPagedResult } from '../../models/get-paged-result.model';
import { AdService } from '../../services/ad.service';
import { AuthService } from '../../services/auth.service';
import { FavoriteService } from '../../services/favorite.service';
import { LoadingService } from '../../services/loading.service';
import { Advertisement } from 'src/app/models/ad.model';
import { FullAdvertisement } from 'src/app/models/full-ad.model';


@Component({
  selector: 'app-profile-favorites-not-authed',
  templateUrl: './profile-favorites-not-authed.component.html',
  styleUrls: ['./profile-favorites-not-authed.component.scss']
})
export class ProfileFavoritesNotAuthedComponent implements OnInit {

  public ads: FullAdvertisement[] = [];
  public pageSize = 5;
  public pageNumber = 1;
  isVisible = false;
  public adsTotal = this.pageSize;

  constructor(private favoriteService: FavoriteService, private adService: AdService, public loadingService: LoadingService, private authService: AuthService, private nzMessageService: NzMessageService) { }

  ngOnInit(): void {

    
    let i: number;
    let adsLocalStorage: string[] = [];
    for(i = 0; i < localStorage.length; i++){
      if(localStorage.key(i)?.startsWith('adId')){
        adsLocalStorage.push(localStorage.getItem(localStorage.key(i)!)!);
      }
    }

    adsLocalStorage.forEach((ad) => {
      
      this.adService.getById(ad, '').subscribe(res => {
        this.ads.push(res);
        

      })

    });
    this.adsTotal = adsLocalStorage.length;

  }


  deleteFromFavorite(adId: string, index: number){
    localStorage.removeItem('adId ' + adId)
      this.nzMessageService.success("Объявление удалено из избранного.");
      this.ads[index].deleted = true;
      this.adsTotal--;
    };
  






















  fallback =
    'data:image/png;base64,';
}
